<?php	function get_all_roofing_list()	{		$sql="SELECT * FROM `".ROOFING."` WHERE 1";		$sql.=" AND `status` = 'Y'";		//echo $sql;		//exit;		$res=mysql_query($sql); 		$html='';		while($row=mysql_fetch_array($res))		{			if(strlen($row['description']) >160)			{				$description=substr($row['description'],0,160)."...";			}			else			{				$description=$row['description'];			}			$html.='<div class="servicesCol">			  <div class="img-thumbnail">				<div class="img-box"><a href="javascript:void(0);" class="imgModal"><img src="'.DOMAIN_NAME.'images/roofing_gallery/'.$row['dfile'].'" alt="services-img1">				  <div class="searchIcon"><img src="images/search-icon.png" alt="search-icon"></div>				  </a> </div>				<h4>'.$row['title'].'</h4>				<p style="height:57px;">'.$description.'</p>				<p class="readMoreBtn"><a href="javascript:void(0);">Read More <i class="fa fa-angle-double-right"></i></a></p>			  </div>			</div>';		}		//echo "A";		//exit;		//print ($html);		return $html;	}?>